import React, { Component } from 'react';
import './App.css';
//import List from "./List";

class List extends React.Component {
  render() {
      return (
          <div className="list_item">
              <p className = "list_text"> 
                  {this.props.items}
              </p>
              <button className = "list_delete_button" onClick={this.props.deleteItem}>Delete</button>
          </div>
      );
  }
}
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      items: [],
    };
  }

  onChange = (event) => {
    this.setState({ term: event.target.value });
  }

  onSubmit = (event) => {
    event.preventDefault();
    this.setState({
      term: "",
      items: [...this.state.items, this.state.term]
    });
  }

  deleteItem = indexToDelete => {
    this.setState(({items}) => ({
        items: items.filter((list, index) => index !== indexToDelete)
    }));
  };

  render() {
    return (
      <div>
        <div className="header">Grocery List</div>
        <form className="App" onSubmit = {this.onSubmit}>
          <input value = {this.state.term} onChange = {this.onChange} />
          <button>Add Item</button>
        </form>
        <div className="list_content">
          {this.state.items.map((item, index) => {
            return <List 
              index = {index}
              item = {item.name}
              deleteItem = {this.deleteItem.bind(this, index)}
              />
          })}
        </div>
        {/*<List items = {this.state.items}/>*/}
      </div>
    );
  }
}

export default App;
