import React, {Component} from 'react';

/*
const List = props => (
    <ul>
        {
            props.items.map((item, index) => <li key = {index}> {item} </li>)
        }
    </ul>
);
*/

class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
        }
    }

    deleteItem = indexToDelete => {
        this.setState(({items}) => ({
            items: items.filter((list, index) => index != indexToDelete)
        }));
    };
}

export default List;
